addObject({name:"blood",length:4,depth:-6,sprite:sprites.get("blood"),setParams(t,e,i){this.opacity=t,this.lifetimeSpeed=e,this.speed=i},step(){this.opacity-=this.lifetimeSpeed,this.opacity<=0&&instanceDestroy(this),this.length<10&&(this.length+=this.speed)},draw(){for(let t=0;t<this.length;t++)drawSprite(this.sprite,this.x,this.y+t,0,1,1,this.opacity)}});